mapper.select:
SELECT ht.*, dc.data_center_name AS dataCenterName, ct.cluster_name as clusterName, su.username as createUserName FROM host_table ht LEFT JOIN data_center_table dc on ht.data_center_id = dc.data_center_id left JOIN cluster_table ct on ht.cluster_id = ct.cluster_id left JOIN sys_user su on ht.create_user_id = su.user_id
AND ht.data_center_id = #{dataCenterId}
AND ht.cluster_id = #{clusterId}
AND ht.host_name like concat('%', #{hostName}, '%')
order by ht.create_time desc LIMIT #{page},#{limit}
